function addItems(){$("#add-item-error").hide();var n=$("#add-item-title").val(),t={title:n};$.post("ToDo/AddItem",t,function(){window.location="/ToDo"}).fail(function(n){if(n&&n.responseJSON){var t=Object.keys(n.responseJSON)[0],i=n.responseJSON[t];$("#add-item-error").text(i).show()}})}$(document).ready(function(){$("#add-item-button").on("click",addItems)});